import React, { Component } from 'react';
import { AuthUserContext } from '../Session';
import { compose } from 'recompose';
import { withFirebase } from '../Firebase';


import 'typeface-roboto';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 500,
    fontSize: 64,
    htmlFontSize: 24,
    color: 'white',
    spacing: theme.spacing(5)
  },
  bar: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
}));
class Account extends Component {
  static contextType = AuthUserContext
  constructor(props) {
    super(props);
    this.state = {
      username: "hey",
      email: "",
      loading: true,
      classes: ''
    };
  }
  async componentDidMount()
  {
    const user = this.context
    console.log(await this.onListenForUser(user.uid));
    var hello = await this.onListenForUser(user.uid);
    this.state.username = hello;
    this.state.classes = this.onStyle();
   this.setState({loading: false});
  }

  onStyle = () =>
  {
    var hello2 = useStyles();
    return hello2
  }
  onListenForUser = (uid) =>
  {
    this.setState({ loading: true });

    var ref = this.props.firebase.user(uid);
    var hello = ref.once("value")
    .then(function(snapshot){
    var newPost = snapshot.val();
    	// console.log(newPost.username);
      // console.log(newPost.username);
     return (newPost.username);
  })
  return (hello);
  };
 render() {
  const {loading, username, email, classes} = this.state;
return (
<AuthUserContext.Consumer>
      {authUser => (
        <div>
          {!loading && (
            <div>
    Username: {username}
     </div>,
       <Box position = "absolute" m={100} left={0} bottom = {500} top = {0} className= {classes.root}>
       <Typography color="textPrimary">
         Username: {username}
       </Typography>
       </Box>
          )}

          {loading && <div>Loading = {authUser.uid} ...</div>
          }
        </div>
      )}
    </AuthUserContext.Consumer>
  );
}
}

const AccountBase = compose(
  withFirebase,
)(Account)
export default AccountBase;
