import React, { Component } from 'react';
import { AuthUserContext } from '../Session';
import { compose } from 'recompose';
import { withFirebase } from '../Firebase';


import 'typeface-roboto';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { createMuiTheme, responsiveFontSizes, ThemeProvider } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 500,
    fontSize: 64,
    htmlFontSize: 24,
    color: 'white',
    spacing: theme.spacing(5)
  },
  bar: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
}));
let theme = createMuiTheme({
  root: {
    flexGrow: 1,
  },
  typography: {
    h1: {
      fontSize: 50,
      lineHeight: 1.5,
    },
    h2: {
      fontSize: 25,
      lineHeight: 1.5,
    },
    h3: {
      fontSize: 30,
    }
  },
});
theme.typography.h3 = {
  [theme.breakpoints.up('md')]: {
    fontSize: '6rem',
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}
class Account extends Component {
  static contextType = AuthUserContext
  constructor(props) {
    super(props);
    this.state = {
      username: "hey",
      email: "",
      loading: true,
    };
  }
  async componentDidMount()
  {
    const user = this.context
    console.log(await this.onListenForUser(user.uid));
    var hello = await this.onListenForUser(user.uid);
    this.state.username = hello;
   this.setState({loading: false});
  }

  onStyle = () =>
  {
    var hello2 = useStyles();
    return hello2
  }
  onListenForUser = (uid) =>
  {
    this.setState({ loading: true });

    var ref = this.props.firebase.user(uid);
    var hello = ref.once("value")
    .then(function(snapshot){
    var newPost = snapshot.val();
    	// console.log(newPost.username);
      // console.log(newPost.username);
     return (newPost.username);
  })
  return (hello);
  };

  
 render() {
  const {loading, username, email} = this.state;
return (
<AuthUserContext.Consumer>
      {authUser => (
        <div>
          {!loading && (
            <div>
              <div className={theme.root}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper className={theme.paper}>xs=12
       <Typography color="textPrimary" theme = {theme} variant = "h3">
         Username: {username}
       </Typography>
       <Box position = "absolute" right = {0} left={400} bottom = {50} top = {275}>
       <Typography color="textPrimary" theme = {theme} variant = "h3">
         Email: {authUser.email}
       </Typography>
       </Box>
       <Box position = "absolute" right = {0} left={1000} bottom = {0} top = {100}>
       <Typography color="textPrimary" theme = {theme} variant = "h3">
         Profile Picture:
       </Typography>
       </Box></Paper>
        </Grid>   
        </Grid>
    </div>
       
       </div>
          )}

          {loading && <div>Loading = {authUser.uid} ...</div>
          }
        </div>
      )}
    </AuthUserContext.Consumer>
  );
}
}

const AccountBase = compose(
  withFirebase,
)(Account)
export default AccountBase;
