import React, { Component } from 'react';
import { AuthUserContext } from '../Session';
import { compose } from 'recompose';
import { withFirebase } from '../Firebase';
const INITIAL_STATE = {
  username: '',
  email: '',
  picture: '',
  ref: '',
}

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {...INITIAL_STATE };
  }

 handleEntry = ref => {
  ref.once("value")
  .then(function(snapshot){
    this.state.username = snapshot.child("username");
    this.state.email = snapshot.child("email");
  })
 } 
  render() {
  return (
<AuthUserContext.Consumer>
{authUser => (
//this.ref = this.props.firebase.user(authUser.uid),
this.ref = authUser.uid,
<div>
  Username: {this.ref}
</div>
)}
</AuthUserContext.Consumer>






      
    );
  }


}

const AccountBase = compose(
  withFirebase,
)(Account)
export default AccountBase;
