import app from 'firebase/app';
import 'firebase/auth';

const config = {
    apiKey: "AIzaSyB6MfPPiiYYNHSMAb-QCSgwv7wrv9N2vJs",
    authDomain: "silichatapp.firebaseapp.com",
    databaseURL: "https://silichatapp.firebaseio.com",
    projectId: "silichatapp",
    storageBucket: "silichatapp.appspot.com",
    messagingSenderId: "659589044302",
    appId: "1:659589044302:web:4a5676c49a8986aa6ecb1f",
    measurementId: "G-WE3F4G7SKB"
  };

  class Firebase {
    constructor() {
      app.initializeApp(config);

      this.auth = app.auth();
      this.db = app.database();
      this.st = app.storage();
    }

    // Auth API 
    doCreateUserWithEmailAndPassword = (email, password) =>
    this.auth.createUserWithEmailAndPassword(email, password);

    doSignInWithEmailAndPassword = (email, password) =>
    this.auth.signInWithEmailAndPassword(email, password);

    doSignOut = () => this.auth.signOut();

    doPasswordReset = email => this.auth.sendPasswordResetEmail(email);
    doPasswordUpdate = password =>
    this.auth.currentUser.updatePassword(password);

    // Chatroom API
    handleNameChange = e => this.setState({ nickname: e.target.value });
    handleEmailChange = e => this.setState({ email: e.target.value });
    handleClick = e => {
      // register the nickname
      this.db.ref().child('nicknames').push({
        nickname: this.state.nickname,
        email: this.state.email,
      });
      this.setState({ joined: true });
    };
    handleMsgChange = e => this.setState({ msg: e.target.value });
  handleKeyDown = e => {
    if (e.key === "Enter") {
      // send the msg
      this.chatRoom.push({
        sender: this.state.nickname,
        msg: this.state.msg,
      });
      // clear the field
      this.setState({ msg: "" });
    }
  };
  }
  export default Firebase;
