import React, { Component } from 'react';
import { AuthUserContext } from '../Session';
import { compose } from 'recompose';
import { withFirebase } from '../Firebase';
class Account extends Component {
  static contextType = AuthUserContext
  constructor(props) {
    super(props);
    this.state = {
      username: "hey",
      email: "",
      loading: true,
    };
  }
  async componentDidMount()
  {
    const user = this.context
    console.log(await this.onListenForUser(user.uid));
    this.state.username = await this.onListenForUser(user.uid);
    console.log(this.state.username);
  // this.setState({loading: false});
  }

  onListenForUser = (uid) =>
  {
    this.setState({ loading: true });

    var ref = this.props.firebase.user(uid);
    var hello = ref.once("value")
    .then(function(snapshot){
    var newPost = snapshot.val();
    	// console.log(newPost.username);
      // console.log(newPost.username);
     return (newPost.username);
  })
  return (hello);
  };

 handleEntry = ref => {

  ref.once("value")
  .then(function(snapshot){
    var newPost = snapshot.val();
    	console.log(newPost.username);
   	 console.log(newPost.username);
  })
  this.state.loading = false;
 }

 render() {
  const {loading, username, email} = this.state;
return (
<AuthUserContext.Consumer>
      {authUser => (
        <div>
          {!loading && (
            <div>
    Username: {username}
     </div>
          )}

          {loading && <div>Loading = {authUser.uid} ...</div>
          }
        </div>
      )}
    </AuthUserContext.Consumer>
  );
}
}

const AccountBase = compose(
  withFirebase,
)(Account)
export default AccountBase;
