import React, { Component } from 'react';
import ReactPlayer from 'react-player'
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import TextField from '@material-ui/core/TextField';
import './Player.css';
import { withFirebase } from '../Firebase';

export default class ResponsivePlayer extends Component {
  constructor(props) {
        super(props);
        console.log(this.props);
      this.state = {
        source: 'https://www.youtube.com/watch?v=foiD5iCkq08',
        newSource: 'qwe',
        player: '',
        playing: true
      }
        this.play = this.play.bind(this);
        this.handleValueChange = this.handleValueChange.bind(this);
        this.onTextChange = this.onTextChange.bind(this);
      //  this.Player = this.props.firebase.db.ref().child('chatrooms').child('global');
      }

play(url) {   
 
    console.log(this.state.player.props.muted);
    this.setState({
      source: 'https://www.youtube.com/watch?v=foiD5iCkq08'
    }); 
    console.log(this.state.player);

}
handleValueChange(e) {
  this.setState({
    source: this.state.newSource
  });
  e.preventDefault();
  console.log(this.state.newSource);
}
onTextChange = event => {
  console.log(event.target.value);
  this.setState({ [event.target.name]: event.target.value });
};
  render () {
    const {
      newSource
    } = this.state;
    return (
      
      <div className='player-wrapper'>
        <ReactPlayer
       ref={player => {
                    this.state.player = player;
                  }}
          className='react-player'
          url={this.state.source}
          controls
          playing ={this.state.playing}
          width = "1000px"
          height = "720px"
        />
      <div className = 'buttons'>
      <Button onClick={this.play}>
            Link #2
          </Button>
      </div>
      <form onSubmit={this.handleValueChange}>
      <input value = {newSource} 
      onChange={this.onTextChange} 
      type="text" 
      name="newSource">
        </input>
      </form>
      </div>
      
    )
  }
}
