import React, { Component } from 'react';
import { AuthUserContext } from '../Session';
import { compose } from 'recompose';
import { withFirebase } from '../Firebase';
var hi = "nice"
class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      email: "",
      loading: true,
    };
  }
  componentDidMount()
  {
  this.onListenForUser(authUser)
  }

  onListenForUser = (authUser) =>
  {
    this.setState({ loading: true });

    var ref = this.props.firebase.user(authUser.uid);
    ref.once("value")
    .then(function(snapshot){
    var newPost = snapshot.val();
    	console.log(newPost.username);
   	 console.log(newPost.username);
      hi = newPost.username;
      this.setState({loading: false});
  })
  };

 handleEntry = ref => {

  ref.once("value")
  .then(function(snapshot){
    var newPost = snapshot.val();
    	console.log(newPost.username);
   	 console.log(newPost.username);
  	  hi = newPost.username;
  })
  this.state.loading = false;
 } 
 render() {
  const {loading} = this.state;
return (
<AuthUserContext.Consumer>
      {authUser => (
        <div>
          {!loading && (
            <div>
    Username: {hi}
  </div>,
  console.log(authUser.uid)
          )}

          {loading && (<div>Loading = {authUser.uid} ...</div>,
          console.log(this.ref)
          )}
        </div>
      )}
    </AuthUserContext.Consumer>
  );
}
}

const AccountBase = compose(
  withFirebase,
)(Account)
export default AccountBase;
